import './App.css';
import {useState , useEffect} from 'react';

const App = () => {
  const [counter , setCounter]= useState(0);

  useEffect( () => {
    setCounter(100);
  }, []); // when we put [] empty thsi means that the initial of the counter 
          //will happen only one time at the beginning and this is very important 
          //because when we don't do it we can not modify the state by buttons
  return (
    <div className="App">
      App
    </div>
  );
}

export default App;
